statements.select:
select x.* from ( select top ($PageNo$*$PageSize$) row_number() over(Order By $OrderByClause$) as SN, a.*,b.FeeType,b.Remain,(CASE WHEN b.FeeType ='00002' THEN dbo.FUNC_Oca_CeilingHour(a.ServeManArriveTime,a.ServeManLeaveTime) WHEN b.FeeType ='00001' then 1.0 END) AS tResult from Oca_ServiceWorkOrder a left join Oca_EPay_OldManAccount b on a.OldManId=b.OldManId and a.ServeItemB=b.ServeItemB
a.AreaId=#AreaId# a.Status=#Status# a.DoStatus=#DoStatus# $WhereClause$
)x where x.SN > ($PageNo$-1)*$PageSize$
