statements.select:
select x.* from ( select top ($PageNo$*$PageSize$) row_number() over(Order By $OrderByClause$) as SN,n.* from( select info.OldManId,base.OldManName,base.Gender,base.IDNo,base.AreaId,base.AreaId2,base.AreaId3, base.Address,base.Tel,base.Mobile,info.Times from (select a.OldManId,COUNT(a.OldManId) as Times from Oca_OldManComplainInfo a where a.Status=1 group by a.OldManId) info left join dbo.Oca_OldManBaseInfo base on info.OldManId=base.OldManId
base.OldManName=#OldManName# base.Gender=#Gender# base.IDNo=#IDNo# base.AreaId=#AreaId# base.AreaId2=#AreaId2# base.AreaId3=#AreaId3# base.Address=#Address# base.Tel=#Tel# base.Mobile=#Mobile# base.InputCode1=#InputCode1# base.InputCode2=#InputCode2# $WhereClause$
) n
Times>=#TimesFrom# Times <=#TimesTo#
)x where x.SN > ($PageNo$-1)*$PageSize$
