statements.select:
select x.* from ( select top ($PageNo$*$PageSize$) row_number() over(Order By $OrderByClause$) as SN, * From (select a.*,b.IDNo,b.Gender,b.OldManName,b.Mobile,b.Tel,b.InputCode1,b.InputCode2, b.Address,b.StationId,c.SickBedNo,d.ExtNo,d.FloorNo,d.RoomNo,e.ItemName,x.UserName,y.CallNo From Pam_WorkExecute a inner join Oca_OldManBaseInfo b on a.OldManId=b.OldManId and ISNULL(b.StationId,'')<>'' and b.StationId<>'null' and b.Status=3 and b.ResidentStatus=7 inner join Pam_RoomOldMan c on b.OldManId=c.OldManId and a.RoomId=c.RoomId and GETDATE() between c.BeginDate and c.EndDate inner join Pam_Room d on c.RoomId = d.RoomId and d.Status=1 inner join Sys_DictionaryItem e on a.WorkItem=e.ItemId and e.DictionaryId='13001' inner join Pub_User x on a.UserId = x.UserId left join Pam_ServeMan y on a.UserId = y.UserId left join Pam_UserMappingRoom z on z.RoomId=a.RoomId and x.UserId=z.UserId
a.CheckInTime>=#CheckInTime_Start# a.CheckInTime<=#CheckInTime_End# a.ServeManArriveTime>=#ServeManArriveTime_Start# a.ServeManArriveTime<=#ServeManArriveTime_End# a.ServeManLeaveTime>=#ServeManLeaveTime_Start# a.ServeManLeaveTime<=#ServeManLeaveTime_End# a.RemindTime>=#RemindTime_Start# a.RemindTime<=#RemindTime_End# a.PlanEndTime>=#PlanEndTime_Start# a.PlanEndTime<=#PlanEndTime_End# a.LastScanTime>=#LastScanTime_Start# a.LastScanTime<=#LastScanTime_End#
) m
Id=#Id# UserId=#UserId# RoomId=#RoomId# OldManId=#OldManId# WorkItem=#WorkItem# Reminded=#Reminded# RemindMax=#RemindMax# PlayRepeats=#PlayRepeats# OldManName=#OldManName# Gender=#Gender# IDNo=#IDNo# Address=#Address# Tel=#Tel# Mobile=#Mobile# InputCode1=#InputCode1# InputCode2=#InputCode2# StationId=#StationId# SickBedNo=#SickBedNo# FloorNo=#FloorNo# RoomNo=#RoomNo# ExtNo=#ExtNo# CallNo=#CallNo# $WhereClause$
)x where x.SN > ($PageNo$-1)*$PageSize$
