statements.select:
select x.* from ( select top ($PageNo$*$PageSize$) row_number() over(Order By $OrderByClause$) as SN,u.UserId,u.UserCode,u.UserName,u.UserType,u.Gender as GenderU ,wp.PlanId,WorkItem,dbo.FUNC_Tol_GetDictionaryItemName('13001',wp.WorkItem) WorkItemName, wp.Workload ,CAST( wp.Workload as varchar(40))+' 分钟' as WorkloadName,wp.RemindFlag,wp.RemindRepeats,wp.PlanPeriod ,dbo.FUNC_Tol_GetDictionaryItemName('13002',wp.PlanPeriod) PlanPeriodName,wp.PlanSchedule,wp.Remark as RemarkWP,wp.ServerBeginTime,wp.PlayRepeats ,p.OldManId,p.OldManName,p.Gender as GenderP,p.Birthday,p.IDNo,p.HealthInsuranceFlag,p.HealthInsuranceNumber, p.SocialInsuranceFlag,p.SocialInsuranceNumber,p.LivingState,p.OldManIdentity,p.AreaId,p.AreaId2,p.AreaId3, p.Address,p.LongitudeS,p.LatitudeS,p.PostCode,p.Tel,p.Mobile,p.Remark as RemarkP from Pub_User u right join Pam_WorkPlan wp on u.UserId=wp.UserId left join Oca_OldManBaseInfo p on wp.OldManId=p.OldManId
wp.PlanId=#PlanId# wp.Id=#Id# wp.Status=#Status# wp.CheckInTime>=to_date(#CheckInTime_Start#||' 00:00:00','yyyy-mm-dd HH24:mi:ss') wp.CheckInTime<=to_date(#CheckInTime_End#||' 23:59:59','yyyy-mm-dd HH24:mi:ss') wp.OperatedBy=#OperatedBy# wp.OperatedOn>=to_date(#OperatedOn_Start#||' 00:00:00','yyyy-mm-dd HH24:mi:ss') wp.OperatedOn<=to_date(#OperatedOn_End#||' 23:59:59','yyyy-mm-dd HH24:mi:ss') wp.UserId=#UserId# wp.OldManId=#OldManId# wp.WorkItem=#WorkItem# wp.Workload=#Workload# wp.RemindFlag=#RemindFlag# wp.RemindRepeats=#RemindRepeats# wp.PlanPeriod=#PlanPeriod# wp.PlanSchedule=#PlanSchedule# wp.ServerBeginTime=#ServerBeginTime# wp.Remark=#Remark# PlayRepeats=#PlayRepeats# $WhereClause$
)x where x.SN > ($PageNo$-1)*$PageSize$
