select x.* from ( select top ($PageNo$*$PageSize$) row_number() over(Order By $OrderByClause$) as SN, a.ArticleId,a.Title,a.PublishDate from Pub_Article a inner join Pub_ArticleColumn_Relation b on a.ArticleId= b.ArticleId inner join Pub_ArticleColumn c on b.ColumnId=c.ColumnId and c.Status=1 and a.Status=1 and ISNULL(ExpiredDate,GETDATE())>= GETDATE()
a.AreaId=#AreaId# b.ColumnId=#ColumnId# $WhereClause$
)x where x.SN > ($PageNo$-1)*$PageSize$

