statements.select:
select x.* from ( select top ($PageNo$*$PageSize$) row_number() over(Order By $OrderByClause$) as SN,a.*,b.CallNo,c.OldManName,c.IDNo,c.Address,c.Gender,c.Birthday from (select * from Pub_Reminder where Id in( select MAX(Id) from Pub_Reminder group by SourceKey )) a left join Oca_OldManConfigInfo b on a.SourceKey=b.OldManId left join Oca_OldManBaseInfo c on b.OldManId = c.OldManId
a.OldManId=#OldManId# c.Status=#OldManStatus# c.AreaId=#AreaId# a.RemindTime>=#RemindTime_Start# a.RemindTime<=#RemindTime_End# $WhereClause$
)x where x.SN > ($PageNo$-1)*$PageSize$
